import React from 'react'
import matter from 'gray-matter'
import ReactMarkdown from 'react-markdown'
import BaseLayout from '../../layouts/base.js'
import  NavFAQMobile from "../../layouts/nav-faq-mobile.js";
import  NavFAQ from "../../layouts/nav-faq.js";
import Search from "../../assets/icons/ui/search.js";

export default function FaqTemplate({ faq, data }) {
  // This holds the data between `---` from the .md file
  const frontmatter = data

  return (
    <BaseLayout>
      <div id="page"class="side-nav-page">

        <div class = 'contents bg-grey'> <div class='section center-section'>
          <div class="search-wrapper faq-search">
            <span class="search-icon">
              <span class="search-icon-inner"><Search/></span>
            </span>
            <input class="search" type="search" name="q" placeholder="お困りのキーワードで検索"/>
          </div>
        </div>
        </div>

        <div class='contents'>
        <div class='section faq-wrapper'>

   <NavFAQ/>
     <NavFAQMobile/>

       <div class="faq-inner">
      <h1>{frontmatter.title}</h1>

      <ReactMarkdown source={faq} />
      </div>

    </div>
  </div>
</div>
  </BaseLayout>
  )
}

FaqTemplate.getInitialProps = async (context) => {
  const { slug } = context.query
  // Import our .md file using the `slug` from the URL
  const faq = await import(`../../faq/${slug}.md`)

  // Parse .md data through `matter`
  const data = matter(faq.default)

  // Pass data to the component props
  return { ...data }
}
