import BaseLayout from '../layouts/base.js'
import Link from 'next/link';
import Add from "../assets/icons/ui/add.js";
import  SettingSolid from "../assets/icons/ui/setting_solid.js";
import Instagram from "../assets/icons/social/instagram.js";
import Facebook from "../assets/icons/social/facebook.js";
import Twitter from "../assets/icons/social/twitter.js";
import UserShot from "../layouts/shot_user.js";
import  PenSolid from "../assets/icons/ui/pen_solid.js";
import  CookSolid from "../assets/icons/ui/cook-solid.js";
import  StarSolid from "../assets/icons/ui/star-solid.js";
import  NavEdit from "../layouts/nav-edit.js";


function User() {
  return <img src="images/default/user.jpg" alt="user image" />
}

export default () => (
  <BaseLayout>
  <div id="page">
  <div class="user-row">
  <div class='contents'>
  <div class='section edit-wrapper center-section'>

    <NavEdit/>

<div class="edit-inner">

  {/*項目・プロフィール*/}
  <div class="user-container">
    <div class="user-avatar">
    <User />
    </div>
<div class="user-inner">
        <p class="user-text name">ケリー小田</p>
          <div class = "form-select">
          {/*「どのような内容ですか？」の選択に応じて、placeholderの文を変更する*/}
          <textarea class="feedback-area" placeholder="フィードバックについての場合：Ceaperのサービスを利用して感じた事、良かった点または、改善点などもお話しください。　バグ・不具合についての場合：どのような状況・操作で問題が発生しましたか？ 問題が発生した時の状況を詳しくお話しください。内容に応じて早急に対応させていただきます。"></textarea>
           </div>
        <p class="user-bio">私は、料理が大好きな生粋のキャンパーです。週7で、キャンプに行っています！よく出没する場所は高尾山です。</p>
        <div class="user-social">
        {/*ユーザーのSNSリンク*/}
        <a class="user-social-link">
        <Instagram />
        </a>
        <a class="user-social-link">
        <Facebook />
        </a>
        <a class="user-social-link">
        <Twitter />
        </a>
</div>
    </div>
  </div>

  </div>


  </div>
</div>
</div>
</div>
</BaseLayout>
)
