import React from "react";
import Link from 'next/link';
import  ArrowR from "../assets/icons/ui/arrow-r.js";



class VoteForm extends React.Component {
  state = { Component: null }

  selectYes = () => this.setState({Component: Yes})
  selectGame2 = () => this.setState({Component: Game2})

  render() {
    const {Component} = this.state;
    if(Component) return <Component />;
  return (
     <div className='contents'>

    <div id="vote-wrapper" className="card car-wrapper">
    <h5>この回答はお役に立ちましたか？</h5>
      <div className="button-choice-wrapper">
        <Link href="/edit">
          <button  className="button button-black-ol button-choice-l "onClick={this.selectYes}>はい
        </button></Link>

      <button  className="button  button-black-ol button-choice-r">いいえ
      </button>
        </div>
     </div>

     </div>
  );
}
}
export default VoteForm;


function Yes() {
  return(
<div>
<p>Cepaerを始める前に、あなたのプロフィールを完成させましょう。この項目は、後でも編集が可能です。</p>
</div>
  );
}
