import GSBaseLayout from '../layouts/base-get-started.js'
import Link from 'next/link';
import Upload from "../assets/icons/ui/upload.js";
import Human from "../assets/icons/ui/human.js";
import ArrowR from "../assets/icons/ui/arrow-r.js";
import Circle from "../assets/icons/ui/gs-circle.js";
import StepWizard from 'react-step-wizard';
import  NavPolicies from "../layouts/nav-policies.js";

function User() {
  return <img src="images/default/user.jpg" alt="user image" />
}

export default () => (
  <GSBaseLayout>
  <div id="gs-page">

  <div class='gs-wrapper'>

  <div class='section gs-inner-nav gs-inner bg-grey'>
    <div class='gs-inner-nav-inner'>
<h6 class='title grey xs-text gs-progress'>プロフィールを設定しましょう</h6>

<div class="gs-progress">
  <span class="gs-progress-mark ico"><Circle/></span>
    <span class="gs-progress-line"></span>
    <h5 class="grey">ユーザー名の設定</h5>
</div>

<div class="gs-progress">
  <span class="gs-progress-mark ico"><Circle/></span>
    <span class="gs-progress-line"></span>
    <h5 class="grey">アイコンの設定</h5>
</div>

<div class="gs-progress">
  <span class="gs-progress-mark ico"><Circle/></span>
    <h5 class="grey">完了</h5>
</div>

</div>
</div>

  <div class='section gs-inner-form gs-inner'>
    <div class='gs-inner-form'>
      <div class='gs-inner-form-inner'>

            <StepWizard>
              <Step1 />
              <Step2 />
              <Step3 />
            </StepWizard>

</div>
</div>
</div>

<div class="gs-dots-nav">
  <span class="ico gs-dot"><Circle/></span>
    <span class="ico gs-dot"><Circle/></span>
      <span class="ico gs-dot"><Circle/></span>
</div>

 </div>

 </div>
</GSBaseLayout>
)


const Stats = ({
    currentStep,
    firstStep,
    goToStep,
    lastStep,
    nextStep,
    previousStep,
    totalSteps,
    step,
}) => (
    <div>
        <hr />
        { step > 1 &&
            <button className='btn btn-default btn-block' onClick={previousStep}>Go Back</button>
        }
        { step < totalSteps ?
            <button className='btn btn-primary btn-block' onClick={nextStep}>Continue</button>
            :
            <button className='btn btn-success btn-block' onClick={nextStep}>Finish</button>
        }
        <hr />
        <div style={{ fontSize: '21px', fontWeight: '200' }}>
            <h4>Other Functions</h4>
            <div>Current Step: {currentStep}</div>
            <div>Total Steps: {totalSteps}</div>
            <button className='btn btn-block btn-default' onClick={firstStep}>First Step</button>
            <button className='btn btn-block btn-default' onClick={lastStep}>Last Step</button>
            <button className='btn btn-block btn-default' onClick={() => goToStep(2)}>Go to Step 2</button>
        </div>
    </div>
);

const First = props => {
    const update = (e) => {
        props.update(e.target.name, e.target.value);
    };

    return (
        <div>
            <h3 className='text-center'>Welcome! Have a look around!</h3>

            <label>First Name</label>
            <input type='text' className='form-control' name='firstname' placeholder='First Name'
                onChange={update} />
            <Stats step={1} {...props} />
        </div>
    );
};


function Step2(props) {
  if (props.currentStep !== 2) {
    return null
  }
  return(
    <div className="form-group">
      <label htmlFor="username">Username</label>
      <input
        className="form-control"
        id="username"
        name="username"
        type="text"
        placeholder="Enter username"
        value={props.username}
        onChange={props.handleChange}
        />
    </div>
  );
}

function Step3(props) {
  if (props.currentStep !== 3) {
    return null
  }
  return(
    <React.Fragment>
    <div className="form-group">
      <label htmlFor="password">Password</label>
      <input
        className="form-control"
        id="password"
        name="password"
        type="password"
        placeholder="Enter password"
        value={props.password}
        onChange={props.handleChange}
        />
    </div>
    <button className="btn btn-success btn-block">Sign up</button>
    </React.Fragment>
  );
}
